import React, { useState } from 'react';
import InputForm from './components/InputForm';
import QuestionsDisplay from './components/QuestionsDisplay';
import './styles/App.css';
import mentIcon from './assets/MENT-Icon.jpeg';

function App() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);

  // Simulate API call and generate questions
  const generateQuestions = async (text) => {
    setLoading(true); // Start loading animation
    setTimeout(() => {
      const mockQuestions = [
        "What is the main idea of the paragraph?",
        "How would you summarize the content?"
      ];
      setQuestions(mockQuestions);
      setLoading(false); // End loading animation
    }, 2000); // Simulate 2-second delay
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="top-bar">
          <img src={mentIcon} alt="MENT Logo" className="logo" />
          <span className="header-title">MENT</span>
        </div>
        <h1>Question Generator</h1>
        {loading ? (
          <div className="loader">Loading...</div>
        ) : (
          <>
            <InputForm onGenerate={generateQuestions} />
            <QuestionsDisplay questions={questions} />
          </>
        )}
      </header>
    </div>
  );
}

export default App;
