import React, { useState } from 'react';
import InputForm from './components/Input';
import QuestionsDisplay from './components/QuestionsDisplay';
import './App.css';

function App() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);

  // Function to simulate API call and generate questions
  const generateQuestions = async (text) => {
    setLoading(true); // Start loading animation
    // Simulating an API call with a delay
    setTimeout(() => {
      // Mock questions - Replace with actual API logic
      const mockQuestions = [
        "What is the main idea of the paragraph?",
        "How would you summarize the content?"
      ];
      setQuestions(mockQuestions); // Set the questions in the state
      setLoading(false); // End loading animation
    }, 2000); // Simulate a 2-second delay
  };

  return (
    <div className="App">
      <h1>Question Generator</h1>
      {loading ? (
        <div className="loader">Loading...</div>  // Show loading animation
      ) : (
        <>
          <InputForm onGenerate={generateQuestions} />
          <QuestionsDisplay questions={questions} />
        </>
      )}
    </div>
  );
}

export default App;
