import React, { useState } from 'react';
import InputForm from './components/InputForm';
import Questions from './components/Questions';
import mentIcon from './assets/MENT-Icon.jpeg'; // Path to the MENT logo image
import './styles/App.css';

function App() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);

  const generateQuestions = async (text) => {
    setLoading(true);
    setTimeout(() => {
      const mockQuestions = [
        "What is the main idea of the paragraph?",
        "How would you summarize the content?"
      ];
      setQuestions(mockQuestions);
      setLoading(false);
    }, 2000); // Simulate a 2-second delay
  };

  return (
    <div className="App">
      {/* Top bar with the MENT logo and title aligned to the top-left */}
      <div className="top-bar">
        <img src={mentIcon} alt="MENT Logo" className="logo" />
        <span className="header-title">MENT</span>
      </div>
      <h1>Question Generator</h1>
      <div className="input-form-container">
        <InputForm onGenerate={generateQuestions} />
      </div>

      {/* Only show the questions after they are generated */}
      {!loading && questions.length > 0 && (
        <Questions questions={questions} />
      )}

      {/* Show loader during question generation */}
      {loading && <div className="loader">Generating Questions...</div>}
    </div>
  );
}

export default App;
